(508, '12345', '12', 'welcome\n\n', '#include<iostream>\nint main()\n{\n  for(;;)\n  std::cout<<\"welcome\";\n  return 0;\n}', '', '2023-09-23 10:37:31', '2023-09-23 10:38:44'),
(509, '192111033', '13', 'enter your age \nyour allowed to vote after 9years\n\n', '#include<iostream>\nusing namespace std;\nint main()\n{\n   int n,b;\n   cout<<\"enter your age \"<<endl;\n   cin>>n;\n   if(n<=0)\n   {\n     cout<<\"invalid input \";\n   }\n   else if(n>18)\n   {\n     cout<<\"your allowed to vote \";\n   }\n  else if(n<18)\n  {\n    b=18-n;\n    cout<<\"your allowed to vote after \"<<b<<\"years\";\n  }\n  \n }', '9', '2023-09-23 11:50:16', '2023-09-23 11:57:25'),
(510, '192111665', '40', 'enter the string\n16\n\n', '#include<iostream>\n#include<cstring>\nusing namespace std;\nint main()\n{\n  char s[100];\n  int strlen,i,count;\n  cout<<\"enter the string\"<<endl;\n  cin>>s;\n  for(i=1;i<=strlen[s];i++)\n  {\n   strlen[s];\n    \n  }\n  cout<<strlen;\n  return 0;\n}', 'hariharansrinivas', '2023-09-23 11:51:50', '2023-09-23 14:32:03'),
(546, '192110157', '33', 'enter the radius of circle:5\nenter the length of the square:4\nArea of square:16\nArea of circle:78.5\n\n\n\n', '#include<iostream>\nusing namespace std;\nint main()\n{\n  int area,l,r;\n  cout<<\"enter the radius of circle:5\"<<endl;\n  cin>>r;\n  cout<<\"enter the length of the square:4\"<<endl;\n  cin>>l;\n  {\n  cout<<\"Area of square:16\"<<endl;\n  cout<<\"Area of circle:78.5\"<<endl;\n  cout<<\"\\n\";\n  }\n  return 0;\n}', '5\n4\n', '2023-09-23 13:51:25', '2023-09-23 14:09:59'),
(511, '192110157', '12', 'enter a positive number:123321\nis a palindrome:\n\n\n\n', '#include<iostream>\nusing namespace std;\nint main()\n{\n  int n,num,digit,rev=0;\n  cout<<\"enter a positive number:123321\"<<endl;\n  cin>>n;\n  n=num;\n  while(num!=0)\n  {\n    digit=num%10;\n    rev+=(rev*10)+digit;\n    num=num/10;\n  }\n  if(rev==n);\n  cout<<\"is a palindrome:\"<<endl;\n  cout<<\"\\n\";\n  return 0;\n}', '123321', '2023-09-23 11:53:12', '2023-09-23 14:31:59'),
(521, '192124201', '33', 'enter the radius of the circle= \nenter the side of the square= \nArea of circle= 78.5\n\nArea of square= 16\n\n\n', '#include<iostream>\nusing namespace std;\nint main()\n{\n  int r,rad,a;\n  float pi=3.14;\n  cout<<\"enter the radius of the circle= \";\n  cin>>r;\n  cout<<\"\\nenter the side of the square= \";\n  cin>>a;\n  rad=r*r;\n  float area=pi*rad;\n  int areasq=a*a;\n  cout<<\"\\nArea of circle= \"<<area<<endl;\n  cout<<\"\\nArea of square= \"<<areasq<<endl;\n  return 0;\n}', '5\n4', '2023-09-23 12:23:08', '2023-09-23 14:40:28'),
(512, '192111665', '18', 'enter the stringgoodmoringall\n\n', '#include<iostream>\n#include<cstring>\n#include<algorithm>\nusing namespace std;\nint main()\n{\n  char s[100];\n  int i;\n  cout<<\"enter the string\"<<endl;\n  cin>>s;\n  for(i=0;i<=strlen(s);i++)\n  {\n    if(s[i]>=65 && s[i]<=92)\n    {\n      s[i]=s[i]+32;\n}\n    cout<<s[i];\n}\n\n \n  return 0;\n}', 'GOODMORINGALL', '2023-09-23 11:54:24', '2023-09-23 14:32:05'),
(518, '192210659', '69', 'Enter the size of array : Enter the first array elements : The sorted array is : \n2 \n5 \n7 \n8 \n9 \n\n\n', '#include<iostream>\n#include<cstring>\n#include<algorithm>\n#include<math.h>\nusing namespace std;\nint main ()\n{\n  int n,i,j,temp,a[100];\n  cout<<\"Enter the size of array : \";\n  cin>>n;\n  cout<<\"Enter the first array elements : \";\n  for(i=0;i<n;i++)\n  {\n    cin>>a[i];\n  }\n \n  for(i=0;i<n;i++)\n  {\n    for(j=i+1;j<i;j++)\n    {\n      if(j==0||i==0)\n      {\n        temp=a[i];\n        a[i]=a[j+1];\n        a[j+1]=temp;\n      }\n    }\n  }\n  cout<<\"The sorted array is : \\n\";\n  for(i=0;i<n;i++)\n  {\n    cout<<a[i]<<\" \"<<endl;\n  }\n}', '5\n2\n5\n7\n8\n9\n', '2023-09-23 12:10:13', '2023-09-23 14:21:00'),
(524, '192111665', '4', 'enter the limit\nenter the elemnets of array\n\n\n', '#include<iostream>\nusing namespace std;\nint main()\n{\n  int a[20],i,n;\n  cout<<\"enter the limit\"<<endl;\n  cin>>n;\n  cout<<\"enter the elemnets of array\"<<endl;\n  for(i=0;i<n;i++)\n  {\n   \n      cin>>a[i];\n    \n    \n}\n  cout<<a[i];\n  return 0;\n}', '5\n12\n13\n24\n25\n26', '2023-09-23 12:34:46', '2023-09-23 14:32:07'),
(513, '192111033', '48', 'the add from 1to4is 10\n\n', '#include<iostream>\nusing namespace std;\nint main()\n{\n     int n,n1,sum,i;\n     cin>>n;\n     cin>>n1;\n     for(i=n ;i<=n1;i++)\n     {\n        sum=sum+i;\n     }\n     cout<<\"the add from \"<<n<<\"to\"<<n1<<\"is \"<<sum;\n}', '1\n100', '2023-09-23 12:00:53', '2023-09-23 12:02:52'),
(514, '192210659', '37', 'Enter the number :\n The number is a perfect number .\n\n', '#include<iostream>\nusing namespace std;\nint main ()\n{\n  int num,rem,sum,i;\n  cout<<\"Enter the number :\\n \";\n  cin>>num;\n  for(i=0;i<=num;i++)\n  {\n    if(i%num==0)\n    {\n      sum=sum+i;\n    }\n  }\n  if(sum==num)\n  {\n    cout<<\"The number is a perfect number .\";\n  }\n  else\n  {\n    cout<<\"The number is not a perfect number\";\n  }\n}', '6', '2023-09-23 12:01:52', '2023-09-23 13:54:38'),
(515, '192111665', '9', 'enter the decimal number32\n\n', '#include<iostream>\nusing namespace std;\nint main()\n{\n  float d;\n  int b;\n  cout<<\"enter the decimal number\"<<endl;\n  cin>>d;\n  for(int i=1;i<=d;i++)\n  {\n    b=d/2;\n}\n\n  cout<<\"the binary digit\"<<b;\n  return 0;\n  \n}', '65\n\n\n', '2023-09-23 12:04:35', '2023-09-23 14:32:09'),
(517, '192111033', '190', 'enter the width and height : \nto print the width and height of rectangle : \n\n\n the width is : 2\nthe height is : 3\nto display the area : \n\nthe area of the rectangle is : 6\n\n', '#include<iostream>\nusing namespace std;\n//class rectangle()\n\n   int display_width(int a)\n   {  \n     cin>>a;\n     cout<<\"to print the width and height of rectangle : \\n\"<<endl;\n     cout<<\"\\n the width is : \"<<a;\n     return a;\n   }\n  int display_height(int b)\n  {\n     cin>>b;\n     cout<<\"\\nthe height is : \"<<b;\n     return b;\n  }\n//class area is derived from class rectangle \n  int read_input(int area,int a,int b )\n  {\n      area=a*b;\n      cout<<\"\\nto display the area : \\n\"<<endl;\n      cout<<\"the area of the rectangle is : \"<<area;\n    return 0;\n  }\n\nint main()\n{   \n   int resu,result,are,x,y,area;\n   cout<<\"enter the width and height : \"<<endl;\n   cin>>x>>y;\n   resu=display_width(x);\n   result=display_height(y);\n   area=x*y;\n   are=read_input(area,x,y);\n\n}\n\n ', '2\n3', '2023-09-23 12:07:21', '2023-09-23 12:44:16'),
(516, '192110157', '186', 'enter the value of a:5\nenter the value of b:0\ndivide by zero.....!\n\n\n', '#include<iostream>\nusing namespace std;\nint main()\n{\n  int a=5,b=0,div,i;\n  cout<<\"enter the value of a:5\"<<endl;\n  cin>>a;\n  cout<<\"enter the value of b:0\"<<endl;\n  cin>>b;\n  for(i=0;i>=0;i++)\n  {\n    div=a/b;\n    }\n  cout<<\"divide by zero.....!\";\n  cout<<\"\\n\";\n  return 0;\n}', '0 5', '2023-09-23 12:05:39', '2023-09-23 12:21:41'),
