(542, '192210659', '178', 'Enter the array row :\n Enter the array column :\n Enter the first array element : \nEnter the second array elements :\n The zig-zag pattern is :\n 0 \n0 \n0 \narr[]={4,3,7,8,6,2,1}0 \n0 \n0 \narr[]={4,3,7,8,6,2,1}0 \n0 \n0 \narr[]={4,3,7,8,6,2,1}0 \n0 \n0 \narr[]={4,3,7,8,6,2,1}\n\n', '#include<iostream>\n#include<cstring>\nusing namespace std;\nint main ()\n{\n  int a[10][10],b[10][10],r,c,i,j,k,arr[10][10];\n  cout<<\"Enter the array row :\\n \";\n  cin>>r;\n  cout<<\"Enter the array column :\\n \";\n  cin>>c;\n  cout<<\"Enter the first array element : \\n\";\n  for(i=0;i<r;i++)\n  {\n    for(j=0;j<c;j++)\n    {\n      cin>>a[i][j];\n    }\n  }\n  cout<<\"Enter the second array elements :\\n \";\n  for(i=0;i<r;i++)\n  {\n    for(j=0;j<c;j++)\n    {\n      cin>>b[i][j];\n    }\n  }\n  cout<<\"The zig-zag pattern is :\\n \";\n    for(i=0;i<r;i++)\n    {\n      for(j=0;j<c;j++)\n        arr[i][j]=0;\n      for(k=0;k<c;k++)\n      {\n        arr[i][j]+=a[i][k]*b[k][j];\n      }\n    }\n  //for result \n  for(i=0;i<r;i++)\n  {\n    for(j=0;j<c;j++)\n\n    {\n      cout<<arr[i][j]<<\" \"<<endl;\n    }\n    cout<<\"arr[]={4,3,7,8,6,2,1}\";\n  }\n  return 0;\n}', '4\n3\n7\n8\n6\n2\n1\n', '2023-09-23 13:38:23', '2023-09-23 14:28:52'),
